sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    g_form.clearOptions('course_name');

    var ga = new GlideAjax('GetCourseNameList');
    ga.addParam('sysparm_name', 'getCourseNames');
    ga.addParam('sysparm_course_type', g_form.getValue('course_type'));

    ga.getXML(populateCourseNameList);

    function populateCourseNameList(response) {
        var results = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
        for (var i = 0; i < results.length; i++) {
            g_form.addOption('course_name', results[i], results[i]);
        }
    }
}
