sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var courseNameDropdown = g_form.getControl('course_name');
g_form.clearOptions('course_name');
    var ga = new GlideAjax('GetCourseNameList');
    ga.addParam('sysparm_name', 'getCourseNames');
    ga.addParam('sysparm_course_type', newValue);

    ga.getXMLAnswer(function(response) {
        var courseNames = JSON.parse(response);
        for (var i = 0; i < courseNames.length; i++) {
            g_form.addOption('course_name', courseNames[i], courseNames[i]);
        }
    });
}
