sys_script_include.script:
var GetCourseNameList = Class.create();
GetCourseNameList.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getCourseNames: function() {

        var courseType = this.getParameter('sysparm_course_type');
        var courseNames = [];

        var grCourseType = new GlideRecord('x_1557802_feedba_1_course_type');
        grCourseType.get(courseType);

        var grCourseInformation = new GlideRecord('x_1557802_feedba_1_course_information');

        grCourseInformation.addQuery("course_information.course_type", grCourseType.course_type);
        grCourseInformation.query();

        while (grCourseInformation.next()) {
            courseNames.push(grCourseInformation.getValue("course_name"));
        }
        // for (var e of courseNames) {
        //     gs.info(e);
        // }

        return JSON.stringify(courseNames);
    },

    type: 'GetCourseNameList'
});
