sys_script_include.script:
var updateOrInsert = Class.create();
updateOrInsert.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    handleUpdateOrInsert: function() {
        var flag = "";
        var grUser = new GlideRecord('sys_user');
        grUser.get(this.getParameter('user'));
        var user = grUser.name;

        var grCourseType = new GlideRecord('x_1557802_feedba_1_course_type');
        grCourseType.get(this.getParameter('type'));
        var type = grCourseType.course_type;

        var nameValue = this.getParameter('name');
        var rating = this.getParameter('rating');
        var comment = this.getParameter('comment');

        var gr = new GlideRecord('x_1557802_feedba_1_feedback_data');
        gr.addQuery('u_user.name', user);
        gr.addQuery('course_type.course_type', type);
        gr.addQuery('course_name', nameValue);
        gr.query();

        gs.info("====??????=======user=" + user);
        gs.info("====??????=======type=" + type);
        gs.info("====??????=======nameValue=" + nameValue);
        gs.info("====??????=======rating=" + rating);
        gs.info("====??????=======comment=" + comment);
        try {
            if (gr.next()) {

                gs.info("===========update======gr==" + gr.sys_id);

                gr.setValue('u_rating', rating);
                gr.setValue('comment_for_course', comment);
                // gr.setValue('u_feedback_no', 'AAAAAAAA');

                gr.update();
                gs.info("************finish update");
                flag = "UPDATE";
            } else {

                gs.info("===========insert========");
                //Auto-numbering of new feedbacks 
                var grNo = new GlideRecord('x_1557802_feedba_1_feedback_data');
                grNo.initialize();
                grNo.orderByDesc('u_feedback_no');
                grNo.query();
                var feedbackNo = '';
                if (grNo.next()) {
                    var lastNumber = parseInt(grNo.getValue('u_feedback_no'));
                    var newNumber = (lastNumber + 1).toString().padStart(8, '0');
                    feedbackNo = newNumber;
                    // gs.info("--------------newNumber=" + newNumber);
                } else {
                    feedbackNo = '00000001';
                }

                gs.info("===========feedbackNo========" + feedbackNo);


                var grNew = new GlideRecord('x_1557802_feedba_1_feedback_data');
                grNew.initialize();

                gs.info("====???INSERT???=======user=" + user);
                gs.info("====???INSERT???=======type=" + type);
                gs.info("====???INSERT???=======nameValue=" + nameValue);
                gs.info("====???INSERT???=======rating=" + rating);
                gs.info("====???INSERT???=======comment=" + comment);


                gs.info("===========feedbackNo====feedbackNo====");
                grNew.u_feedback_no = feedbackNo;
                gs.info("===========feedbackNo====userValue====");
                grNew.u_user = this.getParameter('user');
                gs.info("===========feedbackNo====course_type====");
                grNew.course_type = this.getParameter('type');
                gs.info("===========feedbackNo====nameValue====");
                grNew.course_name = nameValue;
                gs.info("===========feedbackNo====rating====");
                grNew.u_rating = rating;
                gs.info("===========feedbackNo====comment====");
                grNew.comment_for_course = comment;
                gs.info("===========feedbackNo====grNew.insert();====");
                grNew.insert();
                gs.info("************finish insert");
                flag = "INSERT";
            }
        } catch (e) {
            gs.error("!!!!!!!!!Error:" + e);
        }
        gs.info("====88888888888=======flag=" + flag);
        return flag;
    }
});
